const array = [1, 2, 3, 4, 5, 6, 7];

function deleteElements(arrays, numbers){
  let arrIndex = arrays.indexOf(numbers);
  arrays.splice(arrIndex, 1);
  // console.log(number, ' було видалено!')
}

deleteElements(array, 5);
console.log(array);


